#include <iostream>
#include <vector>
#include <map>

using namespace std;


/// Sorting + Trie
/// Time Complexity: O(n * |s|^2)
/// Space Complexity: O(n * |s|)
class Trie {

private:
    class Node{

    public:
        vector<Node*> next;
        bool is_word = false;

        Node() : next(26, nullptr) {};
    };

    Node* root;
    const int NEG_INF = -1000000;

public:
    Trie() : root(new Node()) {}

    void insert(const string& word) {

        Node* cur = root;
        for(char c: word){
            if(cur->next[c - 'a'] == nullptr)
                cur->next[c - 'a'] = new Node();
            cur = cur->next[c - 'a'];
        }
        cur->is_word = true;
    }

    bool ok(const string& s){
        return query(root, s, 0) >= 2;
    }

private:
    int query(Node* node, const string& s, int index) {

        if(index == s.size())
            return (node && node->is_word) ? 1 : NEG_INF;

        int res = NEG_INF;
        if(node->next[s[index] - 'a'])
            res = max(res, query(node->next[s[index] - 'a'], s, index + 1));

        if(node->is_word)
            res = max(res, 1 + query(root, s, index));

        return res;
    }
};

class Solution {
public:
    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {

        sort(words.begin(), words.end(), [](const string& s1, const string& s2){
            return s1.size() < s2.size();
        });

        Trie trie;
        vector<string> res;
        for(const string& s: words) {
            if(s == "") continue;
            else if(trie.ok(s)) res.push_back(s);
            else trie.insert(s);
        }
        return res;
    }
};


void print_vec(const vector<string>& res){
    for(const string& e: res) cout << e << ' '; cout << '\n';
}

